insert into motivations (content, author, user_id) VALUES
    ('А если ты не уверен в себе ничего хорошего никогда не получится. Ведь если ты в себя не веришь, кто же поверит?', 'Кто-то умный', 1),
    ('Без идеи не может быть ничего великого! Без великого не может быть ничего прекрасного.', 'Гюстав Флобер', 1),
    ('Быстрее всего учишься в трех случаях — до 7 лет, на тренингах, и когда жизнь загнала тебя в угол.', 'Стивен Кови', 1),
    ('В вашем подсознании скрыта сила, способная перевернуть мир.', 'Уильям Джеймс', 1),
    ('В моем словаре нет слова «невозможно».', 'Наполеон Бонапарт', 1),
    ('Важно верить, что талант нам даётся не просто так – и что любой ценой его нужно для чего-то использовать.', 'Мари Кюри', 1),
    ('Ваше время ограничено, не тратьте его, живя чужой жизнью', 'Стив Джобс', 1),
    ('Велики те, кто видит, что миром правят мысли.', 'Ральф Эмерсон', 1),
    ('Великие души обладают волей, слабые же имеют только желания.', 'Китайская пословица', 1),
    ('Великие умы ставят перед собой цели; остальные люди следуют своим желаниям.', 'Вашингтонг Ирвинг', 1),
    ('Вместо того, чтобы сетовать, что роза имеет шипы, я радуюсь тому, что среди шипов растет роза.', 'Жозеф Жубер', 1),
    ('Во-первых, не делай ничего без причины и цели. Во-вторых, не делай ничего, что бы не клонилось на пользу общества.', 'Марк Аврелий', 1),
    ('Возникшие желания должны реализовываться безотлагательно. А то все удовольствие пропадает. Захотел – сделал, чего тянуть, жизнь коротка. Здесь и сейчас!', 'Михаил Веллер', 1),
    ('Воин действует, а глупец протестует.', 'Мирный воин', 1),
    ('Воин не отказывается от того, что любит. Он находит любовь в том, что делает.', 'Мирный воин', 1),
    ('Вопрос не в том, кто мне разрешит, а в том, кто сможет мне запретить.', 'Айн Рэнд', 1),
    ('Все дело в мыслях. Мысль — начало всего. И мыслями можно управлять. И поэтому главное дело совершенствования: работать над мыслями.', 'Лев Толстой', 1),
    ('Все дети - художники. Проблема в том, чтобы остаться художником, когда ты вырос.', 'Пабло Пикассо', 1),
    ('Все победы начинаются с победы над самим собой.', 'Леонид Леонов', 1),
    ('Всегда выбирайте самый трудный путь - на нем вы не встретите конкурентов.', 'Шарль де Голль', 1),
    ('Всегда опирайтесь на мысль о том, что ваше собственное решение добиться успеха намного важнее всего другого.', 'Авраам Линкольн', 1),
    ('Всякая мысль подобна тесту, стоит помять ее хорошенько — все из нее сделаешь.', 'Иван Тургенев', 1),
    ('Вы не будете расти, если не будете пытаться совершить что-то за пределами того, что вы уже знаете в совершенстве.', 'Ральф Эмерсон', 1),
    ('Вы не должны сравнивать себя с другими, и если природа создала вас летучей мышью, вы не должны пытаться стать страусом.', 'Герман Гессе', 1),
    ('Вы никогда не будете слишком стары для того. Чтобы поставить перед собой новую цель или мечтать о чем-то новом.', 'Клайв Стейплз Льюис', 1),
    ('Вы никогда не пересечете океан, если не наберетесь мужества потерять берег из виду.', 'Христофор Колумб', 1),
    ('Вы никогда не сумеете решить возникшую проблему, если сохраните то же мышление и тот же подход, который привел вас к этой проблеме.', 'Альберт Эйнштейн', 1),
    ('Вы хотите знать кто вы? Не спрашивайте. Действуйте! Действие будет описывать и определять вас.', 'Томас Джефферсон', 1),
    ('Выживает не самый сильный, а самый восприимчивый к переменам.', 'Чарльз Дарвин', 1),
    ('Главное – отдать все силы избранному делу и от вас пойдет молва. Ибо совершенство – редкость.', 'Андре Моруа', 1),
    ('Говорят, что мотивация длится недолго. Что ж, свежесть после ванны – тоже. Поэтому заботиться о них стоит ежедневно.', 'Зиг Зиглар', 1),
    ('Даже если вы очень талантливы и прилагает большие усилия, для некоторых результатов просто требуется время: вы не получите ребенка через месяц, даже если заставите забеременеть девять женщин.', 'Уоррен Баффет', 1),
    ('Два самых важных дня в твоей жизни: день, когда ты появился на свет, и день, когда понял, зачем.', 'Марк Твен', 1),
    ('Два самых важных дня в твоей жизни: день, когда ты появился на свет, и день, когда понял, зачем.', 'Марк Твен', 1),
    ('Делай все, что можешь, там, где ты находишься, используя все, что имеешь.', 'Теодор Рузвельт', 1),
    ('Делай сегодня то, что другие не хотят, завтра будешь жит так, как другие не могут.', 'Кто-то умный', 1),
    ('Делай, что можешь, с тем, что у тебя есть, и там, где ты находишься.', 'Теодор Рузвельт', 1),
    ('Делайте дела с теми людьми, которые вам нравятся и которые разделяют ваши цели.', 'Уоррен Баффет', 1),
    ('Для нас не должно существовать никаких пределов.', 'Ричард Бах', 1),
    ('Единственное счастье в жизни — это постоянное стремление вперед.', 'Эмиль Золя', 1),
    ('Единственный способ сделать что-то очень хорошо – любить то, что ты делаешь.', 'Стив Джобс', 1),
    ('Если вам предлагают место в ракетоносителе, не спрашивайте, что за место! Просто займите его.', 'Шерил Сэндберг, операционный директор Facebook', 1),
    ('Если внутренний голос говорит вам, что вы не можете рисовать – рисуйте как можно больше, тогда этот голос затихнет.', 'Винсент Ван Гог', 1),
    ('Если вы думаете о том, что имеете в жизни, вы всегда сможете иметь больше. Если же вы считаете, чего у вас нет, вам никогда не будет достаточно.', 'Опра Уинфри', 1),
    ('Если вы думаете, что на что-то способны, вы правы; если думаете, что у вас ничего не получится - вы тоже правы.', 'Генри Форд', 1),
    ('Если вы откажитесь от своей мечты, то что отсанется?', 'Джим Керри', 1),
    ('Если вы хотите иметь успех, вы должны выглядеть так, как будто вы его имеете.', 'Томас Мор', 1),
    ('Если вы цените то, что у вас есть в жизни, вы всегда будете получать еще больше. Если думать лишь о том, чего у вас нет – вам никогда не будет достаточно.', 'Опра Уинфри', 1),
    ('Если нет ветра, беритесь за вёсла.', 'Латинская поговорка', 1),
    ('Если останавливаться всякий раз, когда тебя оскорбляют или в тебя плюются, то ты никогда не дойдешь до места, куда тебе надо попасть.', 'Тибор Фишер', 1),
    ('Если тебе не нравится то, что ты получаешь, измени то, что ты даешь.', 'Карлос Кастанеда', 1),
    ('Если ты не научишься управлять собой, тобой будут управлять другие.', 'Хасай Алиев', 1),
    ('Если ты хочешь построить корабль, не надо созывать людей, планировать, делить работу, доставать инструменты. Надо заразить людей стремлением к бесконечному морю. Тогда они сами построят корабль.', 'Антуан де Сент-Экзюпери', 1),
    ('Если хотите добиться успеха, задайте себе 4 вопроса: Почему? А почему бы и нет? Почему бы и не я? Почему бы и не прямо сейчас?', 'Джимми Дин', 1),
    ('Если хочешь продлить свою жизнь, укороти свои трапезы.', 'Бенджамин Франклин', 1),
    ('Есть только один способ избежать критики: ничего не делайте, ничего не говорите и будьте никем.', 'Аристотель', 1),
    ('Есть только один способ проделать большую работу – полюбить ее!', 'Стив Джобс', 1),
    ('Еще не все колеса изобретены: мир слишком удивителен, чтобы сидеть сложа руки.', 'Ричард Бренсон', 1),
    ('Желать и ждать – на этом далеко не уедешь. Встань и начни следовать за своей мечтой.', 'Джаред Лето', 1),
    ('Жизнь - это то, что с тобой происходит, пока ты строишь планы.', 'Джон Леннон', 1),
    ('Жизнь – это большой холст и вы должны бросить на него всю краску какую только можете.', 'Дэнни Кэй', 1),
    ('Жизнь измеряется не количеством наших вдохов, а количеством моментов, от которых перехватывает дахание.', 'Майя Энджелоу', 1),
    ('Жизнь меряется не тем, сколько в ней лет, а тем, сколько в этих годах настоящей жизни.', 'Авраам Линкольн', 1),
    ('За свою карьеру я пропустил более 9000 бросков, проиграл почти 300 игр. 26 раз мне доверяли сделать финальный победный бросок, и я промахивался. Я терпел поражения снова, и снова, и снова. И именно поэтому я добился успеха.', 'Майкл Джордан', 1),
    ('Завтра – первый чистый лист книги в 365 страниц. Напиши хорошую книгу.', 'Брэд Пейсли', 1),
    ('Запомните, никто не может заставить вас чувствовать себя неполноценным без вашего на то согласия.', 'Элеонор Рузвельт', 1),
    ('Запомните, что не достичь успеха – иногда тоже большая удача.', 'Далай Лама', 1),
    ('Зачастую говорят, что мотивации хватает ненадолго. Но то же самое происходит и с освежающим душем, поэтому и рекомендуют его принимать ежедневно.', 'Зиг Зиглар, писатель, бизнесмен и консультант по маркетингу', 1),
    ('Знаний недостаточно, ты должен применять их. Желаний недостаточно, ты должен делать.', 'Брюс Ли', 1),
    ('Идите уверенно по направлению к мечте. Живите той жизнью, которую вы сами себе придумали.', 'Генри Дэвид Торо, писатель, мыслитель, натуралист', 1),
    ('Или вы управляете днем или день управляет вами.', 'Джим Рон', 1),
    ('Инвестиции в знания дают самые высокие девиденды.', 'Бенджамин Франклин', 1),
    ('Искусство быть счастливым заключается в способности находить счастье в простых вещах.', 'Генри Уорд Бичер', 1),
    ('Каждая мечта тебе дается вместе с силами, необходимыми для ее осуществления.', 'Ричард Бах', 1),
    ('Каждый хочет изменить человечество, но никто не задумывается о том, как изменить себя.', 'Лев Толстой', 1),
    ('Как можно быстрее превращайте ваши масштабные мысли в масштабные действия. Не позволяйте ложным отговоркам затормозить вас. Отговорки – симптомы страха.', 'Дональд Трамп', 1),
    ('Как прекрасно, что не нужно ждать ни минуты, чтобы начать делать мир лучше.', 'Анна Франк', 1),
    ('Когда вы вводите какие-либо новшества будтье готовы к тому, что вас назовут сумасшедшим.', 'Ларри Эллисон', 1),
    ('Когда закрывается одна дверь к счастью, тут же открывается другая. Но мы часто так долго смотрим на первую, что не замечаем вторую.', 'Элен Келлер, писательница, лектор, политическая активистка', 1),
    ('Когда кажется, что весь мир настроен против вас, вспомните, что самолет взлетает не по ветру, а против него.', 'Генри Форд', 1),
    ('Когда мне было 5 лет, мама всегда говорила, что главное в жизни – счастье. Когда я пошел в школу, на вопрос, кем я хочу быть, когда вырасту, я ответил “счастливым человеком”. Мне тогда сказали, что я не понимаю вопроса, а я ответил, что это они не понимают жизни.', 'Джон Леннон', 1),
    ('Когда я освобождаюсь от того, кто я есть, я становлюсь тем, кем я могу быть.', 'Лао Цзы', 1),
    ('Кораблю безопасней в порту, но он не для этого строился.', 'Грейс Хоппер', 1),
    ('Либо вы управляете вашим днем, либо день управляет вами.', 'Джим Рон, оратор и бизнес-тренер', 1),
    ('Либо напиши что-нибудь стоящее, либо делай что-нибудь, о чем стоит написать.', 'Бенджамин Франклин', 1),
    ('Логика может привести Вас от пункта А к пункту Б, а воображение — куда угодно.', 'Альберт Эйнштейн', 1),
    ('Лучшая месть – огромный успех.', 'Фрэнк Синатра', 1),
    ('Лучше быть уверенным в хорошем результате, чем надеяться на отличный.', 'Уоррен Баффет', 1),
    ('Лучше освещать одну маленькую свечу, чем проклянать темноту.', 'Китайская пословица', 1),
    ('Лучшее время, чтобы посадить дерево, было 20 лет назад. Следующий подходящий момент - сегодня.', 'Китайская пословица', 1),
    ('Люди забудут, что ты говорил, забудут, что ты делал, но никогда не забудут, что ты заставил их почувствовать.', 'Майя Энджелоу, писательница и поэтесса', 1),
    ('Мечтайте так, как будто вам жить вечно. Живите так, как будто вам умирать сегодня.', 'Джеймс Дин', 1),
    ('Мир делится на два класса — одни веруют в невероятное, другие совершают невозможное.', 'Оскар Уайлд', 1),
    ('Мудрый человек требует всего только от себя, ничтожный же человек требует всего от других.', 'Лев Толстой', 1),
    ('Мы – рабы своих привычек. Измени свои привычки, изменится твоя жизнь.', 'Роберт Кийосаки', 1),
    ('Мы – хозяева своей судьбы. Мы – капитаны своих душ.', 'Уинстон Черчиль', 1),
    ('Мы становимся тем, о чем мы думаем.', 'Эрл Найтингейл, радиоведущий', 1),
    ('Мы являемся тем, что постоянно делаем. Следовательно, совершенство – не действие, а привычка.', 'Аристотель', 1),
    ('Надеюсь, что в конце своих дней, когда я предстану перед Богом, у меня не останется ни капли таланта, чтобы я могла сказать ему: “Я использовала все, что ты дал мне”.', 'Эрма Бомбек, писатель, журналист', 1),
    ('Надо любить жизнь больше, чем смысл жизни.', 'Федор Достоевский', 1),
    ('Настоящая ответственность бывает только личной.', 'Фазиль Искандер', 1),
    ('Настоящее хобби нашего поколения – это нытье и тупая болтовня ни о чем. Неудачные отношения, проблемы с учебой, начальник – мудак. Это все полная фигня. Если у тебя ничего не получается, то есть только один мудак – это ты. И ты сильно удивишься, если узнаешь, как много можно изменить, просто оторвав жопу от дивана.', 'Джордж Карлин', 1),
    ('Наука — это организованные знания, мудрость — это организованная жизнь.', 'Иммануил Кант', 1),
    ('Научитесь говорить “Я не знаю”, и это уже будет прогресс.', 'Моисей Маймонид, еврейский философ и богослов', 1),
    ('Начинайте делать все, что вы можете сделать – и даже то, о чем можете хотя бы мечтать. В смелости гений, сила и магия.', 'Иоганн Вольфганг Гете', 1),
    ('Начинайте копировать то, что вам нравится. Копируйте. Копируйте. Копируйте. И найдете себя.', 'Едзи Ямамото', 1),
    ('Начинать всегда стоит с того, что сеет сомнения.', 'Борис Стругацкий', 1),
    ('Начните оттуда, где вы сейчас находитесь. Используйте то, что у вас есть и делайте все, что можете.', 'Артур Эш, теннисист', 1),
    ('Наша жизнь начинает подходить к концу, когда мы перестаём говорить о действительно важных вещах.', 'Мартин Лютер Кинг-младший', 1),
    ('Наше сознание - это все. Вы становитесь тем, о чем думаете.', 'Будда', 1),
    ('Наши достижения всегда соответствуют нашим амбициям.', 'Андрей Курпатов', 1),
    ('Нашу судьбу формируют именно те маленькие незаметные решения, которые мы принимаем по 100 раз за день.', 'Энтони Роббинс', 1),
    ('Не бойтесь жизни. Поверьте, что жизнь достойна того, чтобы прожить, и вам будет дано по вере вашей.', 'Уильям Джеймс', 1),
    ('Не будь другим, если можешь быть самим собой.', 'Парацельс', 1),
    ('Не ошибается тот, кто ничего не делает! Не бойтесь ошибаться – бойтесь повторять ошибки!', 'Теодор Рузвельт', 1),
    ('Не проблемы должны толкать вас в спину, а вперед вести мечты.', 'Дуглас Эверетт', 1),
    ('Не стоит винить ребенка за то, что он боится темноты. Настоящая трагедия – это когда взрослый человек боится света.', 'Платон', 1),
    ('Не столь важно, как медленно ты идешь, как то, как долго ты идешь, не останавливаясь.', 'Конфуций', 1),
    ('Некоторые вещи могут завладеть вашим вниманием, но сосредоточьтесь лучше на тех, что завладевают вашим сердцем.', 'Индийская поговорка', 1),
    ('Неосмысленная жизнь не стоит того, чтобы жить.', 'Сократ', 1),
    ('Неудача – это просто возможность начать снова, но уже более мудро.', 'Генри Форд', 1),
    ('Ни разу не упасть — не самая большая заслуга в жизни. Главное каждый раз подниматься.', 'Нельсон Мандела', 1),
    ('Никогда не делает ошибок только тот, кто не пробует ничего нового.', 'Альберт Эйнштейн', 1),
    ('Никогда не дооценивайте себя. Все то, что делают другие, можете делать и вы.', 'Брайан Трейси', 1),
    ('Никогда не думайте, что вы уже все знаете. И как бы высоко не оценивали вас, всегда имейте мужество сказать себе: «Я невежда».', 'Иван Павлов, ученый-физиолог', 1),
    ('Одна законченная результативная задача стоит полусотни полузаконченных задач.', 'Малкольм Форбс', 1),
